// Wait for the document to be fully loaded
document.addEventListener("DOMContentLoaded", function () {
    // Get a reference to the form element
    var form = document.getElementById("contactForm");

    // Add a submit event listener to the form
    form.addEventListener("submit", function (event) {
        // Prevent the default form submission behavior
        event.preventDefault();

        // Perform your form validation and submission logic here
        // For example, you can use the Fetch API to send the form data to a server
        // or display a success message to the user.

        // After successful submission, you can display the success message
        document.getElementById("form-message-success").style.display = "block";
    });
});
